import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [paymentFormData, setPaymentFormData] = useState({
    studentId: "",
    amount: "",
    method: "Cash",
    description: "",
  });
  const [feeFormData, setFeeFormData] = useState({
    studentId: "",
    total: "",
    deposit: "",
    monthly: "",
    final: "",
    notes: "",
  });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("recordPayment"); // Toggle between tabs

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        console.log("API Response:", res.data);
        if (Array.isArray(res.data)) {
          setStudents(res.data);
        } else {
          setStudents([]);
          setMessage("‚ö†Ô∏è Unexpected data format from API.");
        }
      } catch (error) {
        console.error("Error fetching students:", error);
        setMessage("‚ùå Failed to load students. Check console for details.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  const handleSearchChange = (e, formType) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(e.target.value);

    if (query.length > 0) {
      const filtered = students.filter(
        (s) =>
          s.fullName?.toLowerCase().includes(query) ||
          s.email?.toLowerCase().includes(query) ||
          `${s.fullName} (${s.email})`.toLowerCase().includes(query)
      );
      setSuggestions(filtered);

      if (filtered.length === 1 && 
          (filtered[0].fullName.toLowerCase() === query ||
           filtered[0].email.toLowerCase() === query ||
           `${filtered[0].fullName} (${filtered[0].email})`.toLowerCase() === query)) {
        if (formType === "payment") {
          handleSelectStudent(filtered[0], "payment");
        } else {
          handleSelectStudent(filtered[0], "fee");
        }
      }
    } else {
      setSuggestions([]);
      if (formType === "payment") {
        setPaymentFormData({ ...paymentFormData, studentId: "" });
      } else {
        setFeeFormData({ ...feeFormData, studentId: "" });
      }
    }
  };

  const handleSelectStudent = (student, formType) => {
    if (formType === "payment") {
      setPaymentFormData({ ...paymentFormData, studentId: student._id });
      setSearchQuery(`${student.fullName} (${student.email})`);
    } else {
      setFeeFormData({ ...feeFormData, studentId: student._id });
      setSearchQuery(`${student.fullName} (${student.email})`);
    }
    setSuggestions([]);
    console.log("Selected student ID:", student._id);
  };

  const handlePaymentChange = (e) => {
    setPaymentFormData({ ...paymentFormData, [e.target.name]: e.target.value });
  };

  const handleFeeChange = (e) => {
    setFeeFormData({ ...feeFormData, [e.target.name]: e.target.value });
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting payment data:", paymentFormData);
    if (!paymentFormData.studentId || !paymentFormData.amount) {
      setMessage("‚ö†Ô∏è Please select a student and enter an amount.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/payments", paymentFormData, {
        headers: { "Content-Type": "application/json" },
      });
      setMessage("‚úÖ Payment recorded successfully!");
      setPaymentFormData({
        studentId: "",
        amount: "",
        method: "Cash",
        description: "",
      });
      setSearchQuery("");
    } catch (error) {
      console.error("Error saving payment:", {
        message: error.message,
        response: error.response ? error.response.data : "No response",
        status: error.response ? error.response.status : "No status",
      });
      setMessage("‚ùå Failed to record payment. Check console for details.");
    }
  };

  const handleFeeSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting fee data:", feeFormData);
    if (!feeFormData.studentId || !feeFormData.total || !feeFormData.deposit || !feeFormData.monthly || !feeFormData.final) {
      setMessage("‚ö†Ô∏è Please fill all fee fields.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/payments/assign-fee", feeFormData, {
        headers: { "Content-Type": "application/json" },
      });
      setMessage("‚úÖ Fee assigned/updated successfully!");
      setFeeFormData({
        studentId: "",
        total: "",
        deposit: "",
        monthly: "",
        final: "",
        notes: "",
      });
      setSearchQuery("");
    } catch (error) {
      console.error("Error assigning fee:", {
        message: error.message,
        response: error.response ? error.response.data : "No response",
        status: error.response ? error.response.status : "No status",
      });
      setMessage("‚ùå Failed to assign fee. Check console for details.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Admin Payments</h2>
      {message && <p style={styles.message}>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <>
          <div style={styles.tabContainer}>
            <button
              style={activeTab === "recordPayment" ? styles.activeTab : styles.tab}
              onClick={() => setActiveTab("recordPayment")}
            >
              Record Payment
            </button>
            <button
              style={activeTab === "assignFee" ? styles.activeTab : styles.tab}
              onClick={() => setActiveTab("assignFee")}
            >
              Assign Fee
            </button>
          </div>

          {activeTab === "recordPayment" ? (
            <form onSubmit={handlePaymentSubmit} style={styles.form}>
              <div style={{ position: "relative", marginBottom: "15px" }}>
                <input
                  type="text"
                  placeholder="Search student by name or email"
                  value={searchQuery}
                  onChange={(e) => handleSearchChange(e, "payment")}
                  style={styles.input}
                />
                {suggestions.length > 0 && (
                  <ul style={styles.suggestions}>
                    {suggestions.map((s) => (
                      <li
                        key={s._id}
                        onClick={() => handleSelectStudent(s, "payment")}
                        onMouseDown={(e) => e.preventDefault()}
                        style={styles.suggestionItem}
                      >
                        {s.fullName} ({s.email})
                      </li>
                    ))}
                  </ul>
                )}
                {searchQuery.trim() !== "" && suggestions.length === 0 && students.length > 0 && (
                  <ul style={styles.suggestions}>
                    <li style={{ padding: "8px", color: "#888" }}>
                      No students found
                    </li>
                  </ul>
                )}
              </div>

              <input
                type="number"
                name="amount"
                placeholder="Enter amount"
                value={paymentFormData.amount}
                onChange={handlePaymentChange}
                style={styles.input}
                required
                min="0"
              />

              <select
                name="method"
                value={paymentFormData.method}
                onChange={handlePaymentChange}
                style={styles.input}
              >
                <option value="Cash">Cash</option>
                <option value="UPI">UPI</option>
                <option value="Card">Card</option>
                <option value="NetBanking">NetBanking</option>
                <option value="Other">Other</option>
              </select>

              <textarea
                name="description"
                placeholder="Description"
                value={paymentFormData.description}
                onChange={handlePaymentChange}
                style={styles.textarea}
              />

              <button type="submit" style={styles.button}>
                Save Payment
              </button>
            </form>
          ) : (
            <form onSubmit={handleFeeSubmit} style={styles.form}>
              <div style={{ position: "relative", marginBottom: "15px" }}>
                <input
                  type="text"
                  placeholder="Search student by name or email"
                  value={searchQuery}
                  onChange={(e) => handleSearchChange(e, "fee")}
                  style={styles.input}
                />
                {suggestions.length > 0 && (
                  <ul style={styles.suggestions}>
                    {suggestions.map((s) => (
                      <li
                        key={s._id}
                        onClick={() => handleSelectStudent(s, "fee")}
                        onMouseDown={(e) => e.preventDefault()}
                        style={styles.suggestionItem}
                      >
                        {s.fullName} ({s.email})
                      </li>
                    ))}
                  </ul>
                )}
                {searchQuery.trim() !== "" && suggestions.length === 0 && students.length > 0 && (
                  <ul style={styles.suggestions}>
                    <li style={{ padding: "8px", color: "#888" }}>
                      No students found
                    </li>
                  </ul>
                )}
              </div>

              <input
                type="number"
                name="total"
                placeholder="Total Fee"
                value={feeFormData.total}
                onChange={handleFeeChange}
                style={styles.input}
                required
                min="0"
              />
              <input
                type="number"
                name="deposit"
                placeholder="Deposit"
                value={feeFormData.deposit}
                onChange={handleFeeChange}
                style={styles.input}
                required
                min="0"
              />
              <input
                type="number"
                name="monthly"
                placeholder="Monthly Fee"
                value={feeFormData.monthly}
                onChange={handleFeeChange}
                style={styles.input}
                required
                min="0"
              />
              <input
                type="number"
                name="final"
                placeholder="Final Payment"
                value={feeFormData.final}
                onChange={handleFeeChange}
                style={styles.input}
                required
                min="0"
              />
              <textarea
                name="notes"
                placeholder="Notes"
                value={feeFormData.notes}
                onChange={handleFeeChange}
                style={styles.textarea}
              />

              <button type="submit" style={styles.button}>
                Assign Fee
              </button>
            </form>
          )}
        </>
      )}
    </div>
  );
};

