import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [formData, setFormData] = useState({
    studentId: "",
    amount: "",
    method: "Cash",
    description: "",
  });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users"); // Explicit backend URL
        console.log("API Response:", res.data); // Log the exact response
        if (Array.isArray(res.data)) {
          setStudents(res.data);
        } else {
          setStudents([]);
          setMessage("‚ö†Ô∏è Unexpected data format from API.");
        }
      } catch (error) {
        console.error("Error fetching students:", error);
        setMessage("‚ùå Failed to load students. Check console for details.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (query.length > 0) {
      const filtered = students.filter(
        (s) =>
          s.fullName?.toLowerCase().includes(query.toLowerCase()) ||
          s.email?.toLowerCase().includes(query.toLowerCase())
      );
      setSuggestions(filtered);

      if (filtered.length === 1 && 
          (filtered[0].fullName.toLowerCase() === query.toLowerCase() ||
           filtered[0].email.toLowerCase() === query.toLowerCase())) {
        handleSelectStudent(filtered[0]);
      }
    } else {
      setSuggestions([]);
      setFormData({ ...formData, studentId: "" });
    }
  };

  const handleSelectStudent = (student) => {
    setFormData({ ...formData, studentId: student._id });
    setSearchQuery(`${student.fullName} (${student.email})`);
    setSuggestions([]);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.studentId) {
      setMessage("‚ö†Ô∏è Please select a student from suggestions.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/payments", formData);
      setMessage("‚úÖ Payment recorded successfully!");
      setFormData({
        studentId: "",
        amount: "",
        method: "Cash",
        description: "",
      });
      setSearchQuery("");
    } catch (error) {
      console.error("Error saving payment:", error);
      setMessage("‚ùå Failed to record payment.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Record Payment</h2>
      {message && <p style={styles.message}>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={{ position: "relative", marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Search student by name or email"
              value={searchQuery}
              onChange={handleSearchChange}
              style={styles.input}
            />
            {suggestions.length > 0 ? (
              <ul style={styles.suggestions}>
                {suggestions.map((s) => (
                  <li
                    key={s._id}
                    onClick={() => handleSelectStudent(s)}
                    onMouseDown={(e) => e.preventDefault()}
                    style={styles.suggestionItem}
                  >
                    {s.fullName} ({s.email})
                  </li>
                ))}
              </ul>
            ) : searchQuery.trim() !== "" ? (
              <ul style={styles.suggestions}>
                <li style={{ padding: "8px", color: "#888" }}>
                  No students found
                </li>
              </ul>
            ) : null}
          </div>

          <input
            type="number"
            name="amount"
            placeholder="Enter amount"
            value={formData.amount}
            onChange={handleChange}
            style={styles.input}
            required
          />

          <select
            name="method"
            value={formData.method}
            onChange={handleChange}
            style={styles.input}
          >
            <option value="Cash">Cash</option>
            <option value="UPI">UPI</option>
            <option value="Card">Card</option>
            <option value="Bank Transfer">Bank Transfer</option>
          </select>

          <textarea
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            style={styles.textarea}
          />

          <button type="submit" style={styles.button}>
            Save Payment
          </button>
        </form>
      )}
    </div>
  );
};

