import React, { useState } from "react";
import "./Auth.css";
import { useNavigate } from "react-router-dom";   // ‚úÖ Import navigate

const Login = () => {
  const [formData, setFormData] = useState({ emailOrPhone: "", password: "" });

  const navigate = useNavigate();   // ‚úÖ Initialize navigate hook

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await fetch("http://localhost:5000/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    const data = await res.json();

    if (res.ok) {
      alert("‚úÖ Login successful!");
      console.log("User:", data);

      const role = data.role || (data.user && data.user.role);
      const userId = data._id || (data.user && data.user._id);

      // ‚úÖ Save login info
      if (userId) localStorage.setItem("userId", userId);
      if (role) localStorage.setItem("role", role);

      if (role === "Student") {
        navigate("/student/profile");  // Student
      } else if (role === "Admin") {
        navigate("/dashboard");        // Admin
      } else {
        alert("‚ö†Ô∏è Role not found in response");
      }
    } else {
      alert("‚ö†Ô∏è " + data.error);
    }
  } catch (err) {
    console.error("Error logging in:", err);
    alert("‚ùå Failed to login. Please try again.");
  }
};



  return (
    <div className="auth-container">
      <h2>üîë Login</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group full-width">
          <label>Email / Phone</label>
          <input
            type="text"
            name="emailOrPhone"
            value={formData.emailOrPhone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group full-width">
          <label>Password</label>
          <input      
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-actions">
          <button type="submit" >Login</button>
        </div>

        <p className="auth-links">
          <a href="/ForgotPassword "   >Forgot Password?</a> <br />
                 Don't have account?    <a href="/Signup"> Create Account</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
