import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";

const Sidebar = ({ onLogout }) => {
  const role = localStorage.getItem("role") || "Student"; 

  return (
    <div className="sidebar">
      <h2 className="logo">üéì IT Academy</h2>
      {role === "Student" && (
        <>
          <h3 className="section-title">Student Portal</h3>
          <ul className="nav-links">
            <li><NavLink to="/inquiry">‚ÅâÔ∏è Inquiry</NavLink></li>
            <li><NavLink to="/studentprofileedit">üë§ Profile</NavLink></li>
            <li><NavLink to="/studentapplication">üìù Apply</NavLink></li>
            <li><NavLink to="/studentpayments">üí≥ Payments</NavLink></li>
          </ul>

          {/* ‚úÖ Logout button */}
          <button className="logout-btn" onClick={onLogout}>
            üö™ Logout
          </button>
        </>
      )}
    </div>
  );
};

const StuProfile = () => {
  const [student, setStudent] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.clear(); // clear user data
    navigate("/login"); // redirect to login page
  };

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      console.warn("‚ö†Ô∏è No userId in localStorage. Please log in first.");
      navigate("/login");
      return;
    }

    fetch(`http://localhost:5000/api/users/${userId}`)
      .then(async (res) => {
        const data = await res.json();
        if (!res.ok) throw new Error(data.error || "Request failed");
        setStudent(data);
      })
      .catch((err) => console.error("‚ùå Error fetching student:", err));
  }, [navigate]);

  if (!student) {
    return <div className="page">‚è≥ Loading profile...</div>;
  }

  return (
    <div className="layout">
      <style>{`
        .layout {
          display: flex;
          height: 100vh;
          background: #f9fafc;
          font-family: "Segoe UI", Roboto, Arial, sans-serif;
        }

        .sidebar {
          width: 250px;
          background: linear-gradient(180deg, #4f46e5, #4338ca);
          color: #fff;
          padding: 20px;
          box-shadow: 2px 0 10px rgba(0, 0, 0, 0.08);
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .logo {
          font-size: 22px;
          font-weight: bold;
          margin-bottom: 30px;
          text-align: center;
        }

        .section-title {
          font-size: 14px;
          margin: 15px 0 8px;
          text-transform: uppercase;
          letter-spacing: 1px;
          opacity: 0.8;
        }

        .nav-links {
          list-style: none;
          padding: 0;
          margin: 0;
          flex: 1;
        }

        .nav-links li {
          margin-bottom: 12px;
        }

        .nav-links a {
          text-decoration: none;
          color: #e0e7ff;
          font-size: 16px;
          padding: 10px 14px;
          border-radius: 8px;
          display: block;
          transition: all 0.2s ease-in-out;
        }

        .nav-links a:hover,
        .nav-links a.active {
          background: rgba(255, 255, 255, 0.15);
          color: #fff;
        }

        .logout-btn {
          background: #ef4444;
          color: #fff;
          border: none;
          padding: 10px;
          border-radius: 8px;
          font-size: 16px;
          cursor: pointer;
          margin-top: 20px;
          transition: background 0.3s ease;
        }

        .logout-btn:hover {
          background: #dc2626;
        }

        .page {
          flex: 1;
          padding: 30px;
          overflow-y: auto;
        }

        .page-title {
          font-size: 26px;
          font-weight: bold;
          margin-bottom: 20px;
          color: #1f2937;
        }

        .profile-header {
          display: flex;
          align-items: center;
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
          margin-bottom: 20px;
          transition: transform 0.2s ease;
        }

        .profile-header:hover {
          transform: translateY(-2px);
        }

        .avatar {
          width: 100px;
          height: 100px;
          border-radius: 50%;
          margin-right: 20px;
          border: 3px solid #4f46e5;
        }

        .card {
          background: #fff;
          border-radius: 12px;
          padding: 20px;
          box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
        }

        .card h3 {
          font-size: 18px;
          margin-bottom: 12px;
          color: #4f46e5;
        }

        .card p {
          margin: 6px 0;
          font-size: 15px;
          color: #374151;
        }

        .card strong {
          color: #111827;
        }
      `}</style>

      <Sidebar onLogout={handleLogout} />
      <div className="page">
        <h1 className="page-title">üë§ Student Profile</h1>

        <div className="profile-header">
          <img
            src={`https://i.pravatar.cc/120?u=${student._id}`}
            alt="Student Avatar"
            className="avatar"
          />
          <div>
            <h2>{student.fullName}</h2>
            <p>üìß {student.email}</p>
            <p>üìû {student.phone}</p>
          </div>
        </div>

        <div className="card">
          <h3>Account Info</h3>
          <p><strong>Role:</strong> {student.role}</p>
          <p><strong>User ID:</strong> {student._id}</p>
          <p><strong>Created At:</strong> {new Date(student.createdAt).toLocaleString()}</p>
          <p><strong>Last Updated:</strong> {new Date(student.updatedAt).toLocaleString()}</p>
        </div>
      </div>
    </div>
  );
};

export default StuProfile;
