import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const navigate = useNavigate();

  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) {
      navigate("/login");
      return;
    }
    const fetchProfile = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`);
        const data = await res.json();
        if (res.ok) setStudent(data);
        else alert(data.error);
      } catch (err) {
        console.error("Error loading student:", err);
        alert("Failed to load student data");
      }
    };
    fetchProfile();
  }, [userId, navigate]);

  if (!student) return <p style={{ textAlign: "center", marginTop: "50px" }}>Loading profile...</p>;

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={styles.sidebarTitle}>üéì IT Academy</h2>
        <ul style={styles.sidebarList}>
          <li style={styles.sidebarItem} onClick={() => navigate("/studentprofile")}>Profile</li>
          <li style={styles.sidebarItem}>Courses</li>
          <li style={styles.sidebarItem}>Payments</li>
          <li style={styles.sidebarItem}>Logout</li>
        </ul>
      </div>

      {/* Main content */}
      <div style={styles.main}>
        <h2>üë§ Student Profile</h2>
        <div style={styles.card}>
          <p><strong>Full Name:</strong> {student.fullName}</p>
          <p><strong>Email:</strong> {student.email}</p>
          <p><strong>Phone:</strong> {student.phone}</p>
          <p><strong>Role:</strong> {student.role}</p>
          <p><strong>Joined:</strong> {new Date(student.createdAt).toLocaleDateString()}</p>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    minHeight: "100vh",
    fontFamily: "Arial, sans-serif",
  },
  sidebar: {
    width: "220px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    padding: "20px",
  },
  sidebarTitle: {
    textAlign: "center",
    marginBottom: "20px",
  },
  sidebarList: {
    listStyle: "none",
    padding: 0,
  },
  sidebarItem: {
    padding: "10px 0",
    cursor: "pointer",
  },
  main: {
    flex: 1,
    padding: "30px",
    backgroundColor: "#ecf0f1",
  },
  card: {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 0 10px rgba(0,0,0,0.2)",
    maxWidth: "600px",
  },
};

export default StudentProfile;
