import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [filteredStudents, setFilteredStudents] = useState([]);
  const [formData, setFormData] = useState({
    studentId: "",
    amount: "",
    method: "Cash",
    description: "",
  });
  const [searchQuery, setSearchQuery] = useState("");
  const [message, setMessage] = useState("");

  // Fetch students from backend
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/users?role=student");
        const data = await res.json();
        setStudents(data);
        setFilteredStudents(data); // initially show all
      } catch (err) {
        console.error("Error fetching students:", err);
      }
    };
    fetchStudents();
  }, []);

  // Handle search filter
  useEffect(() => {
    const filtered = students.filter(
      (s) =>
        s.fullName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        s.email.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredStudents(filtered);
  }, [searchQuery, students]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/payments", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        setMessage("‚úÖ Payment recorded successfully!");
        setFormData({ studentId: "", amount: "", method: "Cash", description: "" });
        setSearchQuery("");
      } else {
        setMessage("‚ùå Failed to record payment.");
      }
    } catch (err) {
      console.error("Error saving payment:", err);
      setMessage("‚ùå Server error.");
    }
  };

  return (
    <div style={styles.layout}>
      <Sidebar />
      <div style={styles.page}>
        <h1 style={styles.pageTitle}>üí≥ Payments</h1>
        <p>Here you can assign or record student fees.</p>

        {message && <p>{message}</p>}

        <form onSubmit={handleSubmit} style={styles.form}>
          <label style={styles.label}>Search Student</label>
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Type name or email..."
            style={styles.input}
          />

          <label style={styles.label}>Select Student</label>
          <select
            name="studentId"
            value={formData.studentId}
            onChange={handleChange}
            required
            style={styles.input}
          >
            <option value="">-- Choose Student --</option>
            {filteredStudents.map((s) => (
              <option key={s._id} value={s._id}>
                {s.fullName} ({s.email})
              </option>
            ))}
          </select>

          <label style={styles.label}>Amount</label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            required
            style={styles.input}
          />

          <label style={styles.label}>Payment Method</label>
          <select
            name="method"
            value={formData.method}
            onChange={handleChange}
            style={styles.input}
          >
            <option value="Cash">Cash</option>
            <option value="Card">Card</option>
            <option value="UPI">UPI</option>
            <option value="Bank Transfer">Bank Transfer</option>
          </select>

          <label style={styles.label}>Description</label>
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="e.g. Semester fees"
            style={styles.input}
          />

          <button type="submit" style={styles.button}>üíæ Save Payment</button>
        </form>
      </div>
    </div>
  );
};

const Sidebar = () => (
  <div style={styles.sidebar}>
    <h2 style={styles.logo}>üõ†Ô∏è Admin Panel</h2>
    <ul style={styles.navLinks}>
      <li><NavLink to="/admin/dashboard" style={styles.link}>üìä Dashboard</NavLink></li>
      <li><NavLink to="/admin/inquiries" style={styles.link}>üì© Inquiries</NavLink></li>
      <li><NavLink to="/admin/admissions" style={styles.link}>üéì Admissions</NavLink></li>
      <li><NavLink to="/admin/payments" style={styles.link}>üí≥ Payments</NavLink></li>
      <li><NavLink to="/admin/reports" style={styles.link}>üìë Reports</NavLink></li>
      <li><NavLink to="/admin/users" style={styles.link}>üë• Users / Roles</NavLink></li>
    </ul>
  </div>
);

const styles = {
  layout: { display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" },
  sidebar: { width: "250px", background: "#2c3e50", color: "#fff", padding: "20px" },
  logo: { marginBottom: "20px", fontSize: "22px", fontWeight: "bold" },
  navLinks: { listStyle: "none", padding: 0 },
  link: { display: "block", color: "#ecf0f1", textDecoration: "none", padding: "10px 0", fontSize: "16px" },
  page: { flex: 1, padding: "30px", background: "#ecf0f1", overflowY: "auto" },
  pageTitle: { fontSize: "24px", marginBottom: "20px" },
  form: { background: "#fff", padding: "20px", borderRadius: "8px", maxWidth: "400px" },
  label: { display: "block", margin: "10px 0 5px" },
  input: { width: "100%", padding: "8px", marginBottom: "15px", border: "1px solid #ccc", borderRadius: "4px" },
  button: { background: "#27ae60", color: "#fff", border: "none", padding: "10px 15px", cursor: "pointer", borderRadius: "4px" },
};

export default AdminPayments;
