import React, { useState } from "react";
import Header from "../components/Header";

const ApplicationForm = () => {
  const [formData, setFormData] = useState({
    course: "",
    documents: null,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("✅ Application submitted successfully!");
    console.log(formData);
  };

  return (
    <div className="page">
      <Header title="📝 Application Form" />

      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label>Select Course</label>
          <select name="course" value={formData.course} onChange={handleChange} required>
            <option value="">-- Select --</option>
            <option value="Full Stack Development">Full Stack Development</option>
            <option value="Data Science">Data Science</option>
            <option value="AI/ML">AI/ML</option>
          </select>
        </div>

        <div className="form-group">
          <label>Upload Required Documents</label>
          <input type="file" name="documents" onChange={handleChange} required />
        </div>

        <button type="submit" className="primary-btn">Submit Application</button>
      </form>

      <div className="card">
        <h3>Application Status</h3>
        <p><strong>Current Status:</strong> Under Review</p>
        <p>Last Updated: 2025-08-16</p>
      </div>
    </div>
  );
};

export default ApplicationForm;
