import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";

// ‚úÖ Sidebar component
const Sidebar = ({ onLogout }) => {
  const role = localStorage.getItem("role") || "Student";

  return (
    <div style={styles.sidebar}>
      <h2 style={styles.logo}>üéì IT Academy</h2>
      {role === "Student" && (
        <>
          <h3 style={styles.sectionTitle}>Student Portal</h3>
          <ul style={styles.navLinks}>
            <li><NavLink to="/inquiry" style={styles.navLink}>‚ÅâÔ∏è Inquiry</NavLink></li>
            <li><NavLink to="/studentprofileedit" style={styles.navLink}>üë§ Profile</NavLink></li>
            <li><NavLink to="/studentapplication" style={styles.navLink}>üìù Apply</NavLink></li>
            <li><NavLink to="/studentpayments" style={styles.navLink}>üí≥ Payments</NavLink></li>
          </ul>

          <button style={styles.logoutBtn} onClick={onLogout}>
            üö™ Logout
          </button>
        </>
      )}
    </div>
  );
};

// ‚úÖ Student Profile Page
const StuProfile = () => {
  const [student, setStudent] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      navigate("/login");
      return;
    }

    const fetchStudent = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`);
        const data = await res.json();
        if (!res.ok) throw new Error(data.error || "Failed to fetch student");
        setStudent(data);
      } catch (err) {
        console.error("‚ùå Error fetching student:", err);
        alert("Failed to load student data. Please login again.");
        navigate("/login");
      }
    };

    fetchStudent();
  }, [navigate]);

  if (!student) {
    return <div style={styles.loading}>‚è≥ Loading profile...</div>;
  }

  return (
    <div style={styles.layout}>
      <Sidebar onLogout={handleLogout} />
      <div style={styles.page}>
        <h1 style={styles.pageTitle}>üë§ Student Profile</h1>

        <div style={styles.profileHeader}>
          <img
            src={`https://i.pravatar.cc/120?u=${student._id}`}
            alt="Student Avatar"
            style={styles.avatar}
          />
          <div>
            <h2>{student.fullName}</h2>
            <p>üìß {student.email}</p>
            <p>üìû {student.phone}</p>
          </div>
        </div>

        <div style={styles.card}>
          <h3>Account Info</h3>
          <p><strong>Role:</strong> {student.role}</p>
          <p><strong>User ID:</strong> {student._id}</p>
          <p><strong>Created At:</strong> {new Date(student.createdAt).toLocaleString()}</p>
          <p><strong>Last Updated:</strong> {new Date(student.updatedAt).toLocaleString()}</p>
        </div>
      </div>
    </div>
  );
};

// ‚úÖ Internal CSS
const styles = {
  layout: {
    display: "flex",
    height: "100vh",
    fontFamily: "Segoe UI, Roboto, Arial, sans-serif",
    background: "#f9fafc",
  },
  sidebar: {
    width: "250px",
    background: "linear-gradient(180deg, #4f46e5, #4338ca)",
    color: "#fff",
    padding: "20px",
    boxShadow: "2px 0 10px rgba(0,0,0,0.08)",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  logo: {
    fontSize: "22px",
    fontWeight: "bold",
    marginBottom: "30px",
    textAlign: "center",
  },
  sectionTitle: {
    fontSize: "14px",
    margin: "15px 0 8px",
    textTransform: "uppercase",
    letterSpacing: "1px",
    opacity: 0.8,
  },
  navLinks: {
    listStyle: "none",
    padding: 0,
    margin: 0,
    flex: 1,
  },
  navLink: {
    display: "block",
    textDecoration: "none",
    color: "#e0e7ff",
    fontSize: "16px",
    padding: "10px 14px",
    borderRadius: "8px",
    marginBottom: "12px",
  },
  logoutBtn: {
    background: "#ef4444",
    color: "#fff",
    border: "none",
    padding: "10px",
    borderRadius: "8px",
    fontSize: "16px",
    cursor: "pointer",
    marginTop: "20px",
  },
  page: {
    flex: 1,
    padding: "30px",
    overflowY: "auto",
  },
  pageTitle: {
    fontSize: "26px",
    fontWeight: "bold",
    marginBottom: "20px",
    color: "#1f2937",
  },
  profileHeader: {
    display: "flex",
    alignItems: "center",
    background: "#fff",
    padding: "20px",
    borderRadius: "12px",
    boxShadow: "0 3px 10px rgba(0,0,0,0.08)",
    marginBottom: "20px",
    transition: "transform 0.2s ease",
  },
  avatar: {
    width: "100px",
    height: "100px",
    borderRadius: "50%",
    marginRight: "20px",
    border: "3px solid #4f46e5",
  },
  card: {
    background: "#fff",
    borderRadius: "12px",
    padding: "20px",
    boxShadow: "0 3px 10px rgba(0,0,0,0.08)",
  },
  loading: {
    textAlign: "center",
    marginTop: "50px",
    fontSize: "18px",
  },
};

export default StuProfile;


