import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    studentId: "",
    amount: "",
    method: "Cash",
    description: "",
  });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        console.log("API Response:", res.data);
        if (Array.isArray(res.data)) {
          setStudents(res.data);
        } else {
          setStudents([]);
          setMessage("‚ö†Ô∏è Unexpected data format from API.");
        }
      } catch (error) {
        console.error("Error fetching students:", error);
        setMessage("‚ùå Failed to load students. Check console for details.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(e.target.value);

    if (query.length > 0) {
      const filtered = students.filter(
        (s) =>
          s.fullName?.toLowerCase().includes(query) ||
          s.email?.toLowerCase().includes(query) ||
          `${s.fullName} (${s.email})`.toLowerCase().includes(query)
      );
      setSuggestions(filtered);

      if (filtered.length === 1 && 
          (filtered[0].fullName.toLowerCase() === query ||
           filtered[0].email.toLowerCase() === query ||
           `${filtered[0].fullName} (${filtered[0].email})`.toLowerCase() === query)) {
        handleSelectStudent(filtered[0]);
      }
    } else {
      setSuggestions([]);
      setFormData({ ...formData, studentId: "" });
    }
  };

  const handleSelectStudent = (student) => {
    setFormData({ ...formData, studentId: student._id });
    setSearchQuery(`${student.fullName} (${student.email})`);
    setSuggestions([]);
    console.log("Selected student ID:", student._id); // Debug student selection
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting form data:", formData); // Log form data before submission
    if (!formData.studentId || !formData.amount) {
      setMessage("‚ö†Ô∏è Please select a student and enter an amount.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/payments", formData, {
        headers: { "Content-Type": "application/json" },
      });
      setMessage("‚úÖ Payment recorded successfully!");
      setFormData({
        studentId: "",
        amount: "",
        method: "Cash",
        description: "",
      });
      setSearchQuery("");
    } catch (error) {
      console.error("Error saving payment:", {
        message: error.message,
        response: error.response ? error.response.data : "No response",
        status: error.response ? error.response.status : "No status",
      });
      setMessage("‚ùå Failed to record payment. Check console for details.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Record Payment</h2>
      {message && <p style={styles.message}>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={{ position: "relative", marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Search student by name or email"
              value={searchQuery}
              onChange={handleSearchChange}
              style={styles.input}
            />
            {suggestions.length > 0 && (
              <ul style={styles.suggestions}>
                {suggestions.map((s) => (
                  <li
                    key={s._id}
                    onClick={() => handleSelectStudent(s)}
                    onMouseDown={(e) => e.preventDefault()}
                    style={styles.suggestionItem}
                  >
                    {s.fullName} ({s.email})
                  </li>
                ))}
              </ul>
            )}
            {searchQuery.trim() !== "" && suggestions.length === 0 && students.length > 0 && (
              <ul style={styles.suggestions}>
                <li style={{ padding: "8px", color: "#888" }}>
                  No students found
                </li>
              </ul>
            )}
          </div>

          <input
            type="number"
            name="amount"
            placeholder="Enter amount"
            value={formData.amount}
            onChange={handleChange}
            style={styles.input}
            required
            min="0"
          />

          <select
            name="method"
            value={formData.method}
            onChange={handleChange}
            style={styles.input}
          >
            <option value="Cash">Cash</option>
            <option value="UPI">UPI</option>
            <option value="Card">Card</option>
            <option value="NetBanking">NetBanking</option>
            <option value="Other">Other</option>
          </select>

          <textarea
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            style={styles.textarea}
          />

          <button type="submit" style={styles.button}>
            Save Payment
          </button>
        </form>
      )}
    </div>
  );
};

// Inline CSS
const styles = {
  container: {
    maxWidth: "500px",
    margin: "20px auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    backgroundColor: "#fafafa",
  },
  form: { display: "flex", flexDirection: "column" },
  input: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100%",
    boxSizing: "border-box",
  },
  textarea: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100",
    minHeight: "80px",
    boxSizing: "border-box",
  },
  button: {
    padding: "10px",
    background: "linear-gradient(to right, #6b48ff, #00c4cc)",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontWeight: "bold",
  },
  message: {
    margin: "10px 0",
    fontWeight: "bold",
    color: "#dc3545",
  },
  suggestions: {
    position: "absolute",
    top: "100%",
    left: 0,
    right: 0,
    border: "1px solid #ccc",
    backgroundColor: "#fff",
    borderRadius: "5px",
    listStyle: "none",
    padding: 0,
    margin: 0,
    maxHeight: "150px",
    overflowY: "auto",
    zIndex: 1000,
  },
  suggestionItem: {
    padding: "8px",
    cursor: "pointer",
    "&:hover": {
      backgroundColor: "#f0f0f0",
    },
  },
};

export default AdminPayments;
