import React, { useEffect, useState } from "react";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStudent = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) {
        setError("User not logged in!");
        return;
      }

      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`);
        const data = await res.json();

        if (res.ok) setStudent(data);
        else setError(data.error);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch student data.");
      }
    };

    fetchStudent();
  }, []);

  if (error) return <div style={styles.container}><p style={styles.error}>{error}</p></div>;
  if (!student) return <div style={styles.container}><p>Loading profile...</p></div>;

  return (
    <div style={styles.container}>
      <h2>üéì Student Profile</h2>
      <p><strong>Name:</strong> {student.fullName}</p>
      <p><strong>Email:</strong> {student.email}</p>
      <p><strong>Phone:</strong> {student.phone || "N/A"}</p>
      <p><strong>Role:</strong> {student.role}</p>
    </div>
  );
};

const styles = {
  container: { maxWidth: "500px", margin: "50px auto", padding: "20px", border: "1px solid #ccc", borderRadius: "8px", fontFamily: "Arial" },
  error: { color: "red", fontWeight: "bold" },
};

export default StudentProfile;
