import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    studentId: "",
    amount: "",
    method: "Cash",
    description: "",
  });
  const [message, setMessage] = useState("");

  // Fetch students when component mounts
  // useEffect(() => {
  //   const fetchStudents = async () => {
  //     try {
  //       const res = await axios.get("/api/users");
  //       setStudents(res.data);
  //     } catch (error) {
  //       console.error("Error fetching students:", error);
  //     }
  //   };
  //   fetchStudents();
  // }, []);

  useEffect(() => {
  const fetchStudents = async () => {
    try {
      const res = await axios.get("/api/users");
      console.log("Fetched students:", res.data); // üëà check shape
      setStudents(res.data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };
  fetchStudents();
}, []);


  // Handle search input change
  // const handleSearchChange = (e) => {
  //   const query = e.target.value;
  //   setSearchQuery(query);

  //   if (query.trim().length > 0) {
  //     const filtered = students.filter(
  //       (s) =>
  //         s.fullName?.toLowerCase().includes(query.toLowerCase()) ||
  //         s.email?.toLowerCase().includes(query.toLowerCase())
  //     );
  //     setSuggestions(filtered);
  //   } else {
  //     setSuggestions([]);
  //   }
  // };


  const handleSearchChange = (e) => {
  const query = e.target.value;
  setSearchQuery(query);

  if (query.length > 0) {
    const filtered = students.filter(
      (s) =>
        s.fullName?.toLowerCase().includes(query.toLowerCase()) ||
        s.email?.toLowerCase().includes(query.toLowerCase()) ||
        s.name?.toLowerCase().includes(query.toLowerCase()) // fallback if backend uses `name`
    );
    setSuggestions(filtered);
  } else {
    setSuggestions([]);
  }
};


  // Select student from suggestions
  const handleSelectStudent = (student) => {
    setFormData({ ...formData, studentId: student._id });
    setSearchQuery(`${student.fullName} (${student.email})`);
    setSuggestions([]);
  };

  // Handle form input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.studentId) {
      setMessage("‚ö†Ô∏è Please select a student from suggestions.");
      return;
    }

    try {
      await axios.post("/api/payments", formData);
      setMessage("‚úÖ Payment recorded successfully!");
      setFormData({
        studentId: "",
        amount: "",
        method: "Cash",
        description: "",
      });
      setSearchQuery("");
    } catch (error) {
      console.error("Error saving payment:", error);
      setMessage("‚ùå Failed to record payment.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Record Payment</h2>
      {message && <p style={styles.message}>{message}</p>}

      <form onSubmit={handleSubmit} style={styles.form}>
        {/* Student Search */}
        <div style={{ position: "relative", marginBottom: "15px" }}>
          <input
            type="text"
            placeholder="Search student by name or email"
            value={searchQuery}
            onChange={handleSearchChange}
            style={styles.input}
          />
          {suggestions.length > 0 ? (
            <ul style={styles.suggestions}>
              {suggestions.map((s) => (
                <li
                  key={s._id}
                  onClick={() => handleSelectStudent(s)}
                  onMouseDown={(e) => e.preventDefault()} // prevent blur
                  style={styles.suggestionItem}
                >
                  {s.fullName} ({s.email})
                </li>
              ))}
            </ul>
          ) : (
            searchQuery.trim() !== "" && (
              <ul style={styles.suggestions}>
                <li style={{ padding: "8px", color: "#888" }}>
                  No students found
                </li>
              </ul>
            )
          )}
        </div>

        {/* Amount */}
        <input
          type="number"
          name="amount"
          placeholder="Enter amount"
          value={formData.amount}
          onChange={handleChange}
          style={styles.input}
          required
        />

        {/* Payment Method */}
        <select
          name="method"
          value={formData.method}
          onChange={handleChange}
          style={styles.input}
        >
          <option value="Cash">Cash</option>
          <option value="UPI">UPI</option>
          <option value="Card">Card</option>
          <option value="Bank Transfer">Bank Transfer</option>
        </select>

        {/* Description */}
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          style={styles.textarea}
        />

        {/* Submit */}
        <button type="submit" style={styles.button}>
          Save Payment
        </button>
      </form>
    </div>
  );
};

// Inline CSS
const styles = {
  container: {
    maxWidth: "500px",
    margin: "20px auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    backgroundColor: "#fafafa",
  },
  form: { display: "flex", flexDirection: "column" },
  input: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100%",
  },
  textarea: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100%",
    minHeight: "80px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  message: { margin: "10px 0", fontWeight: "bold" },
  suggestions: {
    position: "absolute",
    top: "100%",
    left: 0,
    right: 0,
    border: "1px solid #ccc",
    backgroundColor: "#fff",
    borderRadius: "5px",
    listStyle: "none",
    padding: 0,
    margin: 0,
    maxHeight: "150px",
    overflowY: "auto",
    zIndex: 1000,
  },
  suggestionItem: {
    padding: "8px",
    cursor: "pointer",
  },
};

export default AdminPayments;
