import React, { useEffect, useState } from "react";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) {
        alert("‚ö†Ô∏è User not logged in");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`);
        const data = await res.json();
        if (res.ok) {
          setStudent(data);
        } else {
          alert("‚ö†Ô∏è " + data.error);
        }
      } catch (err) {
        console.error("Error fetching student profile:", err);
        alert("‚ùå Server error");
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  if (loading) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px", fontSize: "18px" }}>
        ‚è≥ Loading profile...
      </div>
    );
  }

  if (!student) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px", fontSize: "18px" }}>
        ‚ö†Ô∏è Profile not found
      </div>
    );
  }

  return (
    <div style={{ maxWidth: "500px", margin: "50px auto", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", background: "#fafafa" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>üéì Student Profile</h2>
      <p><strong>Full Name:</strong> {student.fullName}</p>
      <p><strong>Email:</strong> {student.email}</p>
      <p><strong>Phone:</strong> {student.phone || "N/A"}</p>
      <p><strong>Role:</strong> {student.role}</p>
      <p><strong>Joined:</strong> {new Date(student.createdAt).toLocaleDateString()}</p>
    </div>
  );
};

export default StudentProfile;
