import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login first");
      navigate("/login");
      return;
    }

    fetch("http://localhost:5000/api/student/profile", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => {
        if (res.status === 401) throw new Error("Unauthorized");
        return res.json();
      })
      .then(data => {
        setStudent(data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError("Failed to load student data. Please login again.");
        localStorage.removeItem("token");
        navigate("/login");
      });
  }, [navigate]);

  if (loading) return <p style={{ padding: "20px" }}>Loading profile...</p>;
  if (error) return <p style={{ padding: "20px", color: "red" }}>{error}</p>;

  return (
    <div style={styles.container}>
      <h2>üë®‚Äçüéì Student Profile</h2>
      <div style={styles.profileCard}>
        <img
          src={student.avatar || "https://i.pravatar.cc/120?u=student"}
          alt="Student Avatar"
          style={styles.avatar}
        />
        <h3>{student.fullName}</h3>
        <p>üìß {student.email}</p>
        <p>üÜî ID: {student._id}</p>
        <p>üìÖ Created: {new Date(student.createdAt).toLocaleDateString()}</p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "600px",
    margin: "50px auto",
    padding: "20px",
    fontFamily: "Arial",
  },
  profileCard: {
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "8px",
    textAlign: "center",
  },
  avatar: { borderRadius: "50%", width: "120px", height: "120px", marginBottom: "10px" },
};

export default StudentProfile;
