import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    studentId: "",
    total: "",
    deposit: "",
    monthly: "",
    final: "",
    notes: "",
  });
  const [feeStatus, setFeeStatus] = useState("Pending");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch all students
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        const data = Array.isArray(res.data) ? res.data : res.data.users || [];
        setStudents(data);
      } catch (err) {
        console.error("Error fetching students:", err);
        setMessage("‚ùå Failed to load students.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  // Handle search input change
  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (!query) {
      setSuggestions([]);
      setFormData((prev) => ({ ...prev, studentId: "" }));
      setFeeStatus("Pending");
      return;
    }

    const filtered = students.filter(
      (s) =>
        s.fullName?.toLowerCase().includes(query.toLowerCase()) ||
        s.email?.toLowerCase().includes(query.toLowerCase())
    );
    setSuggestions(filtered);
  };

  // Handle student selection
  const handleSelectStudent = async (student) => {
    setSearchQuery(`${student.fullName} (${student.email})`);
    setFormData((prev) => ({ ...prev, studentId: student._id }));
    setSuggestions([]);

    try {
      const res = await axios.get(`http://localhost:5000/api/payments/${student._id}`);
      const data = res.data;

      if (data.fees) {
        setFormData({
          studentId: student._id,
          total: data.fees.total || "",
          deposit: data.fees.deposit || "",
          monthly: data.fees.monthly || "",
          final: data.fees.final || "",
          notes: data.fees.notes || "",
        });
        setFeeStatus(data.fees.status || "Pending");
      } else {
        setFormData({
          studentId: student._id,
          total: "",
          deposit: "",
          monthly: "",
          final: "",
          notes: "",
        });
        setFeeStatus("Pending");
      }
    } catch (err) {
      console.error("Error fetching fee info:", err);
      setFeeStatus("Pending");
    }
  };

  // Handle field changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Assign / update fee
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.studentId || !formData.total || !formData.deposit || !formData.monthly || !formData.final) {
      setMessage("‚ö†Ô∏è Please select a student and fill all fee fields.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/payments/assign-fee", formData);
      setMessage("‚úÖ Fee assigned/updated successfully!");
      setFeeStatus(res.data.fee?.status || "Updated");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      console.error("Error assigning fee:", err);
      setMessage("‚ùå Failed to assign fee. Check console.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Manage Student Fees</h2>
      {message && <p style={styles.message}>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <form onSubmit={handleSubmit} style={styles.form}>
          {/* Student Search */}
          <div style={{ position: "relative", marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Search student by name/email"
              value={searchQuery}
              onChange={handleSearchChange}
              style={styles.input}
            />
            {suggestions.length > 0 && (
              <ul style={styles.suggestions}>
                {suggestions.map((s) => (
                  <li
                    key={s._id}
                    onClick={() => handleSelectStudent(s)}
                    onMouseDown={(e) => e.preventDefault()}
                    style={styles.suggestionItem}
                  >
                    {s.fullName} ({s.email})
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Fee Fields */}
          <input type="number" name="total" placeholder="Total Fee" value={formData.total} onChange={handleChange} style={styles.input} required />
          <input type="number" name="deposit" placeholder="Deposit" value={formData.deposit} onChange={handleChange} style={styles.input} required />
          <input type="number" name="monthly" placeholder="Monthly Fee" value={formData.monthly} onChange={handleChange} style={styles.input} required />
          <input type="number" name="final" placeholder="Final Payment" value={formData.final} onChange={handleChange} style={styles.input} required />
          <textarea name="notes" placeholder="Notes" value={formData.notes} onChange={handleChange} style={styles.textarea} />

          {/* Fee Status */}
          <div style={styles.statusContainer}>
            <p>
              <strong>Current Fee Status:</strong> <span style={styles.status}>{feeStatus}</span>
            </p>
          </div>

          {/* Submit Button */}
          <button type="submit" style={styles.button}>Assign/Update Fee</button>
        </form>
      )}
    </div>
  );
};

// Inline CSS
const styles = {
  container: {
    maxWidth: "500px",
    margin: "20px auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    backgroundColor: "#fafafa",
  },
  form: { display: "flex", flexDirection: "column" },
  input: { padding: "10px", marginBottom: "10px", borderRadius: "5px", border: "1px solid #ccc", width: "100%" },
  textarea: { padding: "10px", marginBottom: "10px", borderRadius: "5px", border: "1px solid #ccc", width: "100%", minHeight: "80px" },
  button: { padding: "10px", background: "linear-gradient(to right, #6b48ff, #00c4cc)", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer", fontWeight: "bold" },
  message: { margin: "10px 0", fontWeight: "bold", color: "#dc3545" },
  suggestions: { position: "absolute", top: "100%", left: 0, right: 0, border: "1px solid #ccc", backgroundColor: "#fff", borderRadius: "5px", listStyle: "none", padding: 0, margin: 0, maxHeight: "150px", overflowY: "auto", zIndex: 1000 },
  suggestionItem: { padding: "8px", cursor: "pointer" },
  statusContainer: { marginBottom: "15px", padding: "10px", background: "#f5f6fa", borderRadius: "5px" },
  status: { padding: "6px 10px", borderRadius: "6px", fontSize: "14px", fontWeight: "bold", background: "#ffeaa7" },
};

export default AdminPayments;
