import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ emailOrPhone: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await res.json();

      if (res.ok) {
        localStorage.setItem("userId", data._id);
        localStorage.setItem("role", data.role);

        if (data.role === "Student") navigate("/studentprofile");
        else if (data.role === "Admin") navigate("/admin-profile");
      } else alert(data.error);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>üîë Login</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <label>Email / Phone</label>
        <input type="text" name="emailOrPhone" value={formData.emailOrPhone} onChange={handleChange} style={styles.input} required />

        <label>Password</label>
        <input type="password" name="password" value={formData.password} onChange={handleChange} style={styles.input} required />

        <button type="submit" style={styles.button}>Login</button>
      </form>
    </div>
  );
};

const styles = {
  container: { maxWidth: "400px", margin: "50px auto", padding: "20px", border: "1px solid #ccc", borderRadius: "8px", textAlign: "center", fontFamily: "Arial" },
  form: { display: "flex", flexDirection: "column", gap: "15px", marginTop: "20px" },
  input: { padding: "10px", fontSize: "16px", borderRadius: "5px", border: "1px solid #aaa" },
  button: { padding: "10px", fontSize: "16px", borderRadius: "5px", border: "none", backgroundColor: "#007bff", color: "#fff", cursor: "pointer" },
};

export default Login;
