import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";

const Sidebar = () => {
  const role = localStorage.getItem("role") || "Student"; 
  return (
    <div className="sidebar">
      <h2 className="logo">üéì IT Academy</h2>
      {role === "Student" && (
        <>
          <h3 className="section-title">Student Portal</h3>
          <ul className="nav-links">
            <li><NavLink to="/student/profile">üë§ Profile</NavLink></li>
            <li><NavLink to="/student/application">üìù Apply</NavLink></li>
            <li><NavLink to="/student/payments">üí≥ Payments</NavLink></li>
          </ul>
        </>
      )}
    </div>
  );
};

const StuProfile = () => {
  const [student, setStudent] = useState(null);

  useEffect(() => {
    const userId = localStorage.getItem("userId") || "68b16cd462e9b33d54cb314b";
    fetch(`http://localhost:5000/api/users/${userId}`)
      .then(res => {
        console.log("üì° Response status:", res.status);
        return res.json();
      })
      .then(data => {
        console.log("üì° Student Data:", data);
        setStudent(data);
      })
      .catch(err => console.error("‚ùå Error fetching student:", err));
  }, []);

  if (!student) {
    return <div className="page">‚è≥ Loading profile...</div>;
  }

  return (
    <div className="layout">
      <Sidebar />

      <div className="page">
        <h1 className="page-title">üë§ Student Profile</h1>

        {/* Profile Header */}
        <div className="profile-header">
          <img
            src={`https://i.pravatar.cc/120?u=${student._id}`}
            alt="Student Avatar"
            className="avatar"
          />
          <div>
            <h2>{student.fullName}</h2>
            <p className="student-email">üìß {student.email}</p>
            <p className="student-phone">üìû {student.phone}</p>
          </div>
        </div>

        {/* Personal Info */}
        <div className="card">
          <h3>Account Info</h3>
          <p><strong>Role:</strong> {student.role}</p>
          <p><strong>User ID:</strong> {student._id}</p>
          <p><strong>Created At:</strong> {new Date(student.createdAt).toLocaleString()}</p>
          <p><strong>Last Updated:</strong> {new Date(student.updatedAt).toLocaleString()}</p>
        </div>
      </div>

      {/* ‚úÖ Internal CSS */}
      <style>{`
        .layout {
          display: flex;
          min-height: 100vh;
          font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
          background: #f8fafc;
        }

        /* Sidebar */
        .sidebar {
          width: 240px;
          background: #1e293b;
          color: white;
          padding: 20px;
        }

        .sidebar .logo {
          margin-bottom: 20px;
          font-size: 20px;
          font-weight: bold;
        }

        .section-title {
          font-size: 14px;
          margin: 15px 0 8px;
          color: #cbd5e1;
          text-transform: uppercase;
        }

        .nav-links {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .nav-links li {
          margin: 10px 0;
        }

        .nav-links a {
          text-decoration: none;
          color: #f1f5f9;
          padding: 10px 14px;
          display: block;
          border-radius: 6px;
          transition: background 0.2s ease-in-out;
        }

        .nav-links a.active,
        .nav-links a:hover {
          background: #334155;
        }

        /* Page */
        .page {
          flex: 1;
          padding: 30px;
        }

        .page-title {
          font-size: 26px;
          font-weight: bold;
          margin-bottom: 20px;
          color: #1e293b;
        }

        /* Profile Header */
        .profile-header {
          display: flex;
          align-items: center;
          gap: 20px;
          margin-bottom: 20px;
          background: white;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 2px 6px rgba(0,0,0,0.08);
        }

        .avatar {
          width: 100px;
          height: 100px;
          border-radius: 50%;
          border: 3px solid #007bff;
        }

        .student-email,
        .student-phone {
          font-size: 14px;
          color: #555;
        }

        /* Card */
        .card {
          background: #ffffff;
          padding: 20px;
          margin-bottom: 20px;
          border-radius: 12px;
          box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.08);
        }

        .card h3 {
          margin-bottom: 12px;
          color: #007bff;
          font-size: 18px;
          font-weight: 600;
        }

        .card p {
          margin: 6px 0;
          font-size: 15px;
          color: #444;
        }
      `}</style>
    </div>
  );
};

export default StuProfile;
