import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [formData, setFormData] = useState({ emailOrPhone: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await res.json();

      if (res.ok) {
        alert("‚úÖ Login successful!");
        localStorage.setItem("userId", data._id);
        localStorage.setItem("role", data.role);

        if (data.role === "Student") navigate("/studentprofile");
        else if (data.role === "Admin") navigate("/admin-profile");
        else alert("‚ö†Ô∏è Role not found in response");
      } else {
        alert("‚ö†Ô∏è " + data.error);
      }
    } catch (err) {
      console.error("Error logging in:", err);
      alert("‚ùå Server error during login");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "50px auto", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", background: "#fafafa", textAlign: "center" }}>
      <h2>üîë Login</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
        <input
          type="text"
          name="emailOrPhone"
          value={formData.emailOrPhone}
          onChange={handleChange}
          placeholder="Email or Phone"
          style={{ padding: "10px", marginBottom: "15px", borderRadius: "5px", border: "1px solid #ccc" }}
          required
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Password"
          style={{ padding: "10px", marginBottom: "15px", borderRadius: "5px", border: "1px solid #ccc" }}
          required
        />
        <button type="submit" style={{ padding: "10px", border: "none", borderRadius: "5px", background: "#6b48ff", color: "#fff", fontWeight: "bold", cursor: "pointer" }}>Login</button>
      </form>
      <p style={{ marginTop: "10px" }}>
        <a href="/ForgotPassword">Forgot Password?</a> <br />
        Don't have account? <a href="/Signup">Create Account</a>
      </p>
    </div>
  );
};

export default Login;
