import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import "./Dashboard.css";

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalInquiries: 0,
    totalAdmissions: 0,
    totalRevenue: 0,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/dashboard/stats");
        const data = await res.json();
        setStats(data);
      } catch (err) {
        console.error("‚ùå Error fetching stats:", err);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className="page">
      <Header title="üìä Dashboard" />

      <div className="stats">
        <div className="card">Total Inquiries: {stats.totalInquiries}</div>
        <div className="card">Total Admissions: {stats.totalAdmissions}</div>
        <div className="card">Total Revenue: ‚Çπ{stats.totalRevenue}</div>
      </div>

      <div className="charts">
        <div className="chart">
          <h3>Inquiries Trend (Monthly)</h3>
          <p>[üìà Chart Placeholder]</p>
        </div>
        <div className="chart">
          <h3>Conversion Funnel</h3>
          <p>[üîÑ Funnel Placeholder]</p>
        </div>
        <div className="chart">
          <h3>Revenue Chart</h3>
          <p>[üí∞ Revenue Graph Placeholder]</p>
        </div>
      </div>

      <div className="recent">
        <h3>Recent Activities</h3>
        <ul>
          <li>üì© Inquiry from John Doe</li>
          <li>üí∞ Payment from Priya Sharma</li>
          <li>üéì Admission confirmed for Ramesh</li>
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
