import React, { useEffect, useState } from "react";
import AdminSidebar from "../../components/Sidebar";

const AdminReports = () => {
  const [activeTab, setActiveTab] = useState("enquiry");
  const [enquiries, setEnquiries] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  const [loading, setLoading] = useState(false);

  // ðŸ”¹ Fetch enquiries
  const fetchEnquiries = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/api/enquiries");
      const data = await res.json();
      console.log("Enquiries data:", data);
      setEnquiries(data);
    } catch (err) {
      console.error("Error fetching enquiries:", err);
    } finally {
      setLoading(false);
    }
  };

  // ðŸ”¹ Fetch admissions
  const fetchAdmissions = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/api/applications");
      const data = await res.json();
      console.log("Admissions data:", data);
      setAdmissions(data);
    } catch (err) {
      console.error("Error fetching admissions:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (activeTab === "enquiry") fetchEnquiries();
    else fetchAdmissions();
  }, [activeTab]);

  return (
    <div style={styles.layout}>
      <AdminSidebar />

      <div style={styles.page}>
        <h1 style={styles.title}>ðŸ“‘ Reports</h1>

        {/* ðŸ”¹ Tabs */}
        <div style={styles.tabs}>
          <button
            style={{
              ...styles.tab,
              ...(activeTab === "enquiry" ? styles.activeTab : {}),
            }}
            onClick={() => setActiveTab("enquiry")}
          >
            Enquiry Reports
          </button>
          <button
            style={{
              ...styles.tab,
              ...(activeTab === "admission" ? styles.activeTab : {}),
            }}
            onClick={() => setActiveTab("admission")}
          >
            Admission Reports
          </button>
        </div>

        {/* ðŸ”¹ Content */}
        {loading ? (
          <p>Loading...</p>
        ) : (
          <>
            {activeTab === "enquiry" && (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Full Name</th>
                    <th>Contact</th>
                    <th>Email</th>
                    <th>Course</th>
                    <th>Mode</th>
                    <th>Inquiry Date</th>
                    <th>Source</th>
                    <th>Inquiry Taken By</th>
                  </tr>
                </thead>
                <tbody>
                  {enquiries.length > 0 ? (
                    enquiries.map((item, i) => (
                      <tr key={item._id || i}>
                        <td>{i + 1}</td>
                        <td>{item.fullName}</td>
                        <td>{item.contactNumber}</td>
                        <td>{item.email}</td>
                        <td>{item.courses}</td>
                        <td>{item.mode}</td>
                        <td>
                          {item.inquiryDate
                            ? new Date(item.inquiryDate).toLocaleDateString()
                            : "-"}
                        </td>
                        <td>{item.source}</td>
                        <td>{item.inquiryTakenBy}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="9" style={{ textAlign: "center" }}>
                        No enquiries found.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            )}

            {activeTab === "admission" && (
              <table style={styles.table}>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Registration No</th>
                    <th>Applicant Name</th>
                    <th>Course</th>
                    <th>Batch</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Admission Date</th>
                    <th>Total Fees</th>
                    <th>Deposit</th>
                    <th>Monthly</th>
                  </tr>
                </thead>
                <tbody>
                  {admissions.length > 0 ? (
                    admissions.map((item, i) => (
                      <tr key={item._id || i}>
                        <td>{i + 1}</td>
                        <td>{item.registrationNo}</td>
                        <td>{item.applicantName}</td>
                        <td>{item.course || item.programName}</td>
                        <td>{item.batch}</td>
                        <td>{item.email}</td>
                        <td>{item.phone}</td>
                        <td>
                          {item.admissionDate
                            ? new Date(item.admissionDate).toLocaleDateString()
                            : "-"}
                        </td>
                        <td>â‚¹{item.fees?.total || "-"}</td>
                        <td>â‚¹{item.fees?.deposit || "-"}</td>
                        <td>â‚¹{item.fees?.monthly || "-"}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="11" style={{ textAlign: "center" }}>
                        No admissions found.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            )}
          </>
        )}
      </div>
    </div>
  );
};

// âœ… Clean styles
const styles = {
  layout: {
    display: "flex",
    background: "#f9fafc",
    minHeight: "100vh",
  },
  page: {
    flex: 1,
    padding: "30px",
  },
  title: {
    marginBottom: "20px",
    color: "#374151",
  },
  tabs: {
    display: "flex",
    gap: "10px",
    marginBottom: "20px",
  },
  tab: {
    background: "#e5e7eb",
    border: "none",
    padding: "10px 18px",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: 500,
    transition: "background 0.3s",
  },
  activeTab: {
    background: "#4f46e5",
    color: "white",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    background: "white",
    borderRadius: "10px",
    overflow: "hidden",
    boxShadow: "0 3px 8px rgba(0,0,0,0.08)",
  },
};

export default AdminReports;
