import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudentData = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) {
        alert("‚ö†Ô∏è No user ID found. Please log in again.");
        navigate("/login");
        return;
      }

      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`);
        const data = await res.json();

        if (res.ok) {
          setStudent(data);
        } else {
          console.error("Error fetching student:", data);
          alert("‚ùå Failed to load student data.");
        }
      } catch (err) {
        console.error("Error loading student:", err);
        alert("‚ùå Could not fetch student profile.");
      } finally {
        setLoading(false);
      }
    };

    fetchStudentData();
  }, [navigate]);

  if (loading) {
    return <div style={styles.loading}>‚è≥ Loading profile...</div>;
  }

  if (!student) {
    return <div style={styles.error}>‚ùå No profile found</div>;
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>üë©‚Äçüéì Student Profile</h1>

      <div style={styles.card}>
        <p><strong>Full Name:</strong> {student.fullName}</p>
        <p><strong>Email:</strong> {student.email}</p>
        <p><strong>Phone:</strong> {student.phone || "N/A"}</p>
        <p><strong>Role:</strong> {student.role}</p>
        <p><strong>Joined:</strong> {new Date(student.createdAt).toLocaleString()}</p>
      </div>

      <button
        onClick={() => {
          localStorage.removeItem("userId");
          localStorage.removeItem("role");
          navigate("/login");
        }}
        style={styles.logoutBtn}
      >
        üö™ Logout
      </button>
    </div>
  );
};

// Inline styles
const styles = {
  container: {
    maxWidth: "500px",
    margin: "60px auto",
    padding: "30px",
    borderRadius: "12px",
    backgroundColor: "#fff",
    boxShadow: "0 3px 8px rgba(0,0,0,0.15)",
    textAlign: "center",
  },
  heading: {
    marginBottom: "30px",
  },
  card: {
    textAlign: "left",
    padding: "20px",
    borderRadius: "10px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  loading: {
    textAlign: "center",
    fontSize: "18px",
    marginTop: "50px",
  },
  error: {
    textAlign: "center",
    fontSize: "18px",
    marginTop: "50px",
    color: "#e63946",
  },
  logoutBtn: {
    marginTop: "20px",
    width: "100%",
    padding: "10px",
    border: "none",
    borderRadius: "6px",
    backgroundColor: "#e63946",
    color: "#fff",
    fontSize: "16px",
    cursor: "pointer",
  },
};

export default StudentProfile;
