import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import { NavLink, useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [stats, setStats] = useState({
    inquiries: 0,
    admissions: 0,
    users: 0,
    revenue: 0,
  });

  const [recent, setRecent] = useState({
    inquiries: [],
    admissions: [],
    payments: [],
  });

  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  /* ----------------------------------------------------
     üîπ Fetch Dashboard Stats
  ---------------------------------------------------- */
  useEffect(() => {
    fetch("http://localhost:5000/api/dashboard/stats")
      .then((res) => res.json())
      .then((data) => {
        setStats(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("‚ùå Error fetching dashboard stats:", err);
        setLoading(false);
      });
  }, []);

  /* ----------------------------------------------------
     üîπ Fetch Recent Activities
  ---------------------------------------------------- */
  useEffect(() => {
    fetch("http://localhost:5000/api/dashboard/recent")
      .then((res) => res.json())
      .then((data) => setRecent(data))
      .catch((err) =>
        console.error("‚ùå Error fetching recent activities:", err)
      );
  }, []);

  /* ----------------------------------------------------
     üîπ Logout Handler
  ---------------------------------------------------- */
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* ---------- SIDEBAR ---------- */}
      <div className="sidebar">
        <h2 className="logo">üõ†Ô∏è Admin Panel</h2>
        <ul className="navLinks">
          <li><NavLink to="/Dashboard" className="link">üìä Dashboard</NavLink></li>
          <li><NavLink to="/admin-inquiries" className="link">üì© Inquiries</NavLink></li>
          <li><NavLink to="/admin-admissions" className="link">üéì Admissions</NavLink></li>
          <li><NavLink to="/admin-payments" className="link">üí≥ Payments</NavLink></li>
          <li><NavLink to="/admin/reports" className="link">üìë Reports</NavLink></li>
          <li><NavLink to="/admin/users" className="link">üë• Users / Roles</NavLink></li>
        </ul>
        <button className="logoutBtn" onClick={handleLogout}>üö™ Logout</button>
      </div>

      {/* ---------- MAIN CONTENT ---------- */}
      <div className="page">
        <Header title="üìä Dashboard" />

        {loading ? (
          <p>Loading stats...</p>
        ) : (
          <div className="stats">
            <div className="card">Total Inquiries: {stats.inquiries}</div>
            <div className="card">Total Admissions: {stats.admissions}</div>
            <div className="card">Total Users: {stats.users}</div>
            <div className="card">Total Revenue: ‚Çπ{stats.revenue}</div>
          </div>
        )}

        {/* ---------- RECENT ACTIVITIES ---------- */}
        <div className="recent">
          <h3>Recent Activities</h3>
          <ul>

            {recent.inquiries.map((i) => (
              <li key={i._id}>üì© Inquiry from {i.name}</li>
            ))}

            {recent.payments.map((p) => (
              <li key={p._id}>üí∞ Payment from {p.studentName} ‚Äì ‚Çπ{p.amount}</li>
            ))}

            {recent.admissions.map((a) => (
              <li key={a._id}>üéì Admission confirmed for {a.applicantName}</li>
            ))}

          </ul>
        </div>
      </div>

      {/* ---------- INTERNAL CSS ---------- */}
      <style>{`
        .sidebar {
          width: 240px;
          background: #2c3e50;
          color: #ecf0f1;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          padding: 20px;
        }
        .logo {
          font-size: 22px;
          font-weight: bold;
          text-align: center;
          margin-bottom: 30px;
        }
        .navLinks {
          list-style: none;
          padding: 0;
          flex: 1;
        }
        .navLinks li {
          margin: 12px 0;
        }
        .link {
          color: #ecf0f1;
          text-decoration: none;
          font-size: 16px;
          display: block;
          padding: 8px 12px;
          border-radius: 8px;
          transition: background 0.3s;
        }
        .link:hover, .link.active {
          background: #1abc9c;
        }
        .logoutBtn {
          background: #e74c3c;
          border: none;
          color: white;
          padding: 10px;
          border-radius: 8px;
          cursor: pointer;
          font-size: 15px;
          transition: background 0.3s;
        }
        .logoutBtn:hover {
          background: #c0392b;
        }
        .page {
          flex: 1;
          padding: 20px;
          background: #f9fafc;
        }
        .stats {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
          gap: 20px;
          margin-bottom: 30px;
        }
        .card {
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
          font-size: 18px;
          font-weight: 500;
        }
        .recent {
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
        }
        .recent ul {
          list-style: none;
          padding: 0;
        }
        .recent li {
          padding: 10px 0;
          border-bottom: 1px solid #eee;
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
