import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const StudentProfile = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // ‚úÖ Fetch student data from backend
  useEffect(() => {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      setError("Please login again.");
      return;
    }

    const fetchStudent = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/students/${userId}`);
        const data = await res.json();

        if (res.ok) {
          setStudent(data);
        } else {
          setError(data.error || "Failed to load student data. Please login again.");
        }
      } catch (err) {
        console.error("Error fetching student data:", err);
        setError("Server error while fetching profile data.");
      }
    };

    fetchStudent();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userId");
    localStorage.removeItem("role");
    navigate("/");
  };

  if (error) {
    return (
      <div style={styles.errorContainer}>
        <h2 style={{ color: "red" }}>{error}</h2>
        <button style={styles.retryButton} onClick={() => navigate("/")}>
          üîë Login Again
        </button>
      </div>
    );
  }

  if (!student) {
    return (
      <div style={styles.loadingContainer}>
        <h2>Loading your profile...</h2>
      </div>
    );
  }

  return (
    <div style={styles.layout}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2>üéì IT Academy</h2>
        <ul style={styles.menu}>
          <li style={styles.menuItem}>üè† Dashboard</li>
          <li style={styles.menuItem}>üìö Courses</li>
          <li style={styles.menuItem}>üí≥ Payments</li>
          <li style={styles.menuItem}>üìÑ Applications</li>
          <li style={styles.menuItem}>üë§ Profile</li>
        </ul>
        <button onClick={handleLogout} style={styles.logoutBtn}>
          üö™ Logout
        </button>
      </div>

      {/* Main Profile Section */}
      <div style={styles.main}>
        <h1 style={styles.pageTitle}>üë§ My Profile</h1>

        <div style={styles.card}>
          <p><strong>Full Name:</strong> {student.fullName}</p>
          <p><strong>Email:</strong> {student.email}</p>
          <p><strong>Phone:</strong> {student.phone || "N/A"}</p>
          <p><strong>Role:</strong> {student.role}</p>
          <p><strong>Account Created:</strong> {new Date(student.createdAt).toLocaleString()}</p>
        </div>
      </div>
    </div>
  );
};

const styles = {
  layout: {
    display: "flex",
    height: "100vh",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#f8f9fa",
  },
  sidebar: {
    width: "240px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    padding: "20px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  menu: {
    listStyle: "none",
    padding: 0,
    marginTop: "20px",
  },
  menuItem: {
    padding: "10px 0",
    borderBottom: "1px solid rgba(255,255,255,0.1)",
    cursor: "pointer",
  },
  logoutBtn: {
    backgroundColor: "#e74c3c",
    color: "#fff",
    border: "none",
    padding: "10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  main: {
    flex: 1,
    padding: "40px",
  },
  pageTitle: {
    fontSize: "28px",
    marginBottom: "20px",
  },
  card: {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    lineHeight: "1.8",
  },
  errorContainer: {
    textAlign: "center",
    marginTop: "100px",
  },
  retryButton: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  loadingContainer: {
    textAlign: "center",
    marginTop: "100px",
  },
};

export default StudentProfile;
