import React, { useState } from "react";
import "./Auth.css";
import { useNavigate } from "react-router-dom";   // ‚úÖ Import navigate

const Login = () => {
  const [formData, setFormData] = useState({ emailOrPhone: "", password: "" });

  const navigate = useNavigate();   // ‚úÖ Initialize navigate hook

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await fetch("http://localhost:5000/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });
    const data = await res.json();

    if (res.ok) {
      alert("‚úÖ Login successful!");
      console.log("User:", data);

      // ‚úÖ Check role and redirect accordingly
      if (data.role === "Student") {
        navigate("/inquiryform");   // student goes to inquiryform
      } else if (data.role === "Admin") {
        navigate("/dashboard");     // admin goes to dashboard
      } else {
        navigate("/");              // fallback if role missing
      }
    } else {
      alert("‚ö†Ô∏è " + data.error);
    }
  } catch (err) {
    console.error("Error logging in:", err);
  }
};


  return (
    <div className="auth-container">
      <h2>üîë Login</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group full-width">
          <label>Email / Phone</label>
          <input
            type="text"
            name="emailOrPhone"
            value={formData.emailOrPhone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group full-width">
          <label>Password</label>
          <input      
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-actions">
          <button type="submit" >Login</button>
        </div>

        <p className="auth-links">
          <a href="/ForgotPassword "   >Forgot Password?</a> <br />
                 Don't have account?    <a href="/Signup"> Create Account</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
