import React from "react";
import { NavLink } from "react-router-dom";

const Sidebar = () => {
  const role = localStorage.getItem("role") || "Student"; // Default: Student

  return (
    <div className="sidebar">
      <h2 className="logo">üéì IT Academy</h2>

      {role === "Student" && (
        <>
          <h3 className="section-title">Student Portal</h3>
          <ul className="nav-links">
            <li><NavLink to="/student/profile">üë§ Profile</NavLink></li>
            <li><NavLink to="/student/application">üìù Apply</NavLink></li>
            <li><NavLink to="/student/payments">üí≥ Payments</NavLink></li>
          </ul>
        </>
      )}

      {role === "Admin" && (
        <>
          <h3 className="section-title">Admin CRM</h3>
          <ul className="nav-links">
            <li><NavLink to="/">üìä Dashboard</NavLink></li>
            <li><NavLink to="/inquiries">üì© Inquiries</NavLink></li>
            <li><NavLink to="/pipeline">üìà Admissions Pipeline</NavLink></li>
            <li><NavLink to="/applications">üìù Applications</NavLink></li>
            <li><NavLink to="/payments">üí∞ Payments</NavLink></li>
            <li><NavLink to="/reports">üìë Reports</NavLink></li>
            <li><NavLink to="/users">üë• Users & Roles</NavLink></li>
            <li><NavLink to="/settings">‚öôÔ∏è Settings</NavLink></li>
          </ul>
        </>
      )}
    </div>
  );
};

const StuProfile = () => {
  return (
    <div className="layout">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Page Content */}
      <div className="page">
        <h1 className="page-title">üë§ Student Profile</h1>

        {/* Profile Header */}
        <div className="profile-header">
          <img
            src="https://i.pravatar.cc/120?img=12"
            alt="Student Avatar"
            className="avatar"
          />
          <div>
            <h2>John Doe</h2>
            <p className="student-email">üìß john@example.com</p>
            <p className="student-phone">üìû 9876543210</p>
          </div>
        </div>

        {/* Personal Info */}
        <div className="card">
          <h3>Personal Information</h3>
          <p><strong>Course Interested:</strong> Full Stack Development</p>
          <p><strong>Joining Date:</strong> 2025-08-01</p>
        </div>

        {/* Uploaded Docs */}
        <div className="card">
          <h3>Uploaded Documents</h3>
          <ul>
            <li>ID Proof: ‚úÖ Uploaded</li>
            <li>Marksheet: ‚úÖ Uploaded</li>
            <li>Photo: ‚ùå Not Uploaded</li>
          </ul>
        </div>

        {/* Applications */}
        <div className="card">
          <h3>Applications</h3>
          <table className="table">
            <thead>
              <tr><th>Course</th><th>Status</th><th>Date Applied</th></tr>
            </thead>
            <tbody>
              <tr><td>Full Stack Development</td><td>Shortlisted</td><td>2025-08-10</td></tr>
              <tr><td>Data Science</td><td>Applied</td><td>2025-08-15</td></tr>
            </tbody>
          </table>
        </div>
      </div>

      {/* Internal CSS */}
      <style>{`
        .layout {
          display: flex;
          min-height: 100vh;
          font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        }

        /* Sidebar */
        .sidebar {
          width: 240px;
          background: #1e293b;
          color: white;
          padding: 20px;
        }

        .sidebar .logo {
          margin-bottom: 20px;
          font-size: 20px;
          font-weight: bold;
        }

        .section-title {
          font-size: 14px;
          margin: 10px 0;
          color: #cbd5e1;
        }

        .nav-links {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .nav-links li {
          margin: 10px 0;
        }

        .nav-links a {
          text-decoration: none;
          color: #f1f5f9;
          padding: 8px 12px;
          display: block;
          border-radius: 6px;
          transition: background 0.2s ease-in-out;
        }

        .nav-links a.active,
        .nav-links a:hover {
          background: #334155;
        }

        /* Page */
        .page {
          flex: 1;
          padding: 20px;
          background: #f8fafc;
        }

        .page-title {
          font-size: 24px;
          margin-bottom: 20px;
        }

        /* Profile Header */
        .profile-header {
          display: flex;
          align-items: center;
          gap: 20px;
          margin-bottom: 20px;
          background: white;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 2px 6px rgba(0,0,0,0.1);
        }

        .avatar {
          width: 100px;
          height: 100px;
          border-radius: 50%;
        }

        .student-email,
        .student-phone {
          font-size: 14px;
          color: #555;
        }

        /* Card */
        .card {
          background: #ffffff;
          padding: 20px;
          margin-bottom: 20px;
          border-radius: 12px;
          box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.1);
        }

        .card h3 {
          margin-bottom: 10px;
          color: #007bff;
          font-size: 18px;
        }

        .card p {
          margin: 6px 0;
          font-size: 15px;
          color: #444;
        }

        /* Documents */
        .card ul {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .card ul li {
          padding: 8px 0;
          font-size: 15px;
          border-bottom: 1px solid #eee;
        }

        .card ul li:last-child {
          border-bottom: none;
        }

        /* Table */
        .table {
          width: 100%;
          border-collapse: collapse;
          margin-top: 10px;
        }

        .table th,
        .table td {
          padding: 12px 15px;
          text-align: left;
          border-bottom: 1px solid #eee;
          font-size: 14px;
        }

        .table th {
          background: #007bff;
          color: white;
          font-weight: 600;
        }

        .table tr:hover {
          background: #f1f5ff;
        }
      `}</style>
    </div>
  );
};

export default StuProfile;
