//new one for admin admission form

import React, { useEffect, useState } from "react";

const AdminApplicationForm = () => {
  const [applications, setApplications] = useState([]);
  const [selectedApp, setSelectedApp] = useState(null);
  const [officeData, setOfficeData] = useState({
    registrationNo: "",
    batch: "",
    course: "",
    admissionDate: "",
    status: "Admitted",
  });

  useEffect(() => {
    fetch("http://localhost:5000/api/applications")
      .then((res) => res.json())
      .then(setApplications)
      .catch((err) => console.error("Error:", err));
  }, []);

  const handleSelect = (app) => {
    setSelectedApp(app);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setOfficeData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedApp) return alert("Select a student first!");
    try {
      const res = await fetch(`http://localhost:5000/api/applications/${selectedApp._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(officeData),
      });
      const data = await res.json();
      if (res.ok) alert("‚úÖ Updated successfully!");
      else alert("‚ùå " + data.error);
    } catch {
      alert("‚ö†Ô∏è Could not update.");
    }
  };

  return (
    <div style={styles.page}>
      <h2 style={styles.title}>üìã Admin Admission Form</h2>

      <div style={styles.flex}>
        {/* LEFT: Student list */}
        <div style={styles.list}>
          <h3>Pending Applications</h3>
          {applications.map((app) => (
            <div key={app._id} style={styles.card} onClick={() => handleSelect(app)}>
              <strong>{app.fullName}</strong>
              <p>{app.programName}</p>
            </div>
          ))}
        </div>

        {/* RIGHT: Office details form */}
        <div style={styles.formContainer}>
          {selectedApp ? (
            <form onSubmit={handleSubmit} style={styles.form}>
              <h4>Student Info (Read Only)</h4>
              <p><strong>Name:</strong> {selectedApp.fullName}</p>
              <p><strong>Email:</strong> {selectedApp.email}</p>
              <p><strong>Program:</strong> {selectedApp.programName}</p>

              <h4>Office Details</h4>
              {["registrationNo", "batch", "course", "admissionDate"].map((f) => (
                <div key={f} style={styles.inputGroup}>
                  <label>{f}</label>
                  <input
                    name={f}
                    type={f === "admissionDate" ? "date" : "text"}
                    value={officeData[f]}
                    onChange={handleChange}
                  />
                </div>
              ))}

              <button type="submit" style={styles.button}>Save</button>
            </form>
          ) : (
            <p>Select a student to edit</p>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: { padding: "20px", background: "#f7f9fc", minHeight: "100vh" },
  title: { textAlign: "center", marginBottom: "20px" },
  flex: { display: "flex", gap: "20px" },
  list: { flex: 1, background: "#fff", padding: "15px", borderRadius: "10px", boxShadow: "0 2px 5px rgba(0,0,0,0.1)" },
  card: { padding: "10px", borderBottom: "1px solid #eee", cursor: "pointer" },
  formContainer: { flex: 2, background: "#fff", padding: "20px", borderRadius: "10px", boxShadow: "0 2px 5px rgba(0,0,0,0.1)" },
  form: { display: "flex", flexDirection: "column" },
  inputGroup: { display: "flex", flexDirection: "column", marginBottom: "10px" },
  button: { background: "#00b894", color: "#fff", padding: "10px", border: "none", borderRadius: "5px", cursor: "pointer" },
};

export default AdminApplicationForm;
