import React, { useEffect, useState } from "react";
import Header from "../../components/Header";

const StudentPayments = () => {
  const [fees, setFees] = useState(null);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudentPayments = async () => {
      try {
        // ‚úÖ Replace with logged-in student ID (from auth / context)
        const studentId = "STUDENT001"; 

        const res = await fetch(`http://localhost:5000/api/payments/student/${studentId}`);
        const data = await res.json();

        setFees(data.fees);        // fee structure { total, deposit, monthly, final }
        setPayments(data.payments); // student‚Äôs payments list
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching student payments:", err);
        setLoading(false);
      }
    };

    fetchStudentPayments();
  }, []);

  // ‚úÖ Calculate paid and due
  const totalPaid = payments.reduce((sum, p) => sum + Number(p.amount || 0), 0);
  const totalDue = fees ? Number(fees.total || 0) - totalPaid : 0;

  return (
    <div className="page">
      <Header title="üí≥ My Payments" />

      {/* Internal CSS */}
      <style>{`
        .student-payments {
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0,0,0,0.1);
          margin-top: 20px;
        }
        .fee-card {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
          gap: 15px;
          margin-bottom: 20px;
        }
        .card-box {
          background: #f5f6fa;
          padding: 15px;
          border-radius: 10px;
          text-align: center;
        }
        .card-box h3 {
          margin: 5px 0;
          font-size: 18px;
        }
        .card-box p {
          font-size: 16px;
          font-weight: bold;
          color: #2d3436;
        }
        table {
          width: 100%;
          border-collapse: collapse;
          margin-top: 15px;
        }
        th, td {
          padding: 12px;
          border-bottom: 1px solid #eee;
          text-align: left;
        }
        th {
          background: #f1f2f6;
          font-weight: 600;
        }
        tr:hover {
          background: #f9f9f9;
        }
        .status {
          padding: 6px 10px;
          border-radius: 6px;
          font-size: 14px;
          font-weight: bold;
        }
        .Completed {
          background: #55efc4;
          color: #2d3436;
        }
        .Pending {
          background: #ffeaa7;
          color: #636e72;
        }
        .Failed {
          background: #fab1a0;
          color: #d63031;
        }
      `}</style>

      <div className="student-payments">
        {loading ? (
          <p>‚è≥ Loading payment details...</p>
        ) : !fees ? (
          <p>No fee structure available.</p>
        ) : (
          <>
            {/* Fee Summary */}
            <div className="fee-card">
              <div className="card-box">
                <h3>Total Fees</h3>
                <p>‚Çπ{fees.total}</p>
              </div>
              <div className="card-box">
                <h3>Deposit</h3>
                <p>‚Çπ{fees.deposit}</p>
              </div>
              <div className="card-box">
                <h3>Monthly Fee</h3>
                <p>‚Çπ{fees.monthly}</p>
              </div>
              <div className="card-box">
                <h3>Final Payment</h3>
                <p>‚Çπ{fees.final}</p>
              </div>
              <div className="card-box">
                <h3>Total Paid</h3>
                <p>‚Çπ{totalPaid}</p>
              </div>
              <div className="card-box">
                <h3>Due Amount</h3>
                <p>‚Çπ{totalDue}</p>
              </div>
            </div>

            {/* Payments History */}
            <h3>Payment History</h3>
            {payments.length === 0 ? (
              <p>No payments found.</p>
            ) : (
              <table>
                <thead>
                  <tr>
                    <th>Amount (‚Çπ)</th>
                    <th>Status</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {payments.map((p) => (
                    <tr key={p._id}>
                      <td>{p.amount}</td>
                      <td>
                        <span className={`status ${p.status}`}>{p.status}</span>
                      </td>
                      <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default StudentPayments;
