import React, { useState } from "react";
import "./inquiry_form.css";

const InquiryForm = () => {
  const [formData, setFormData] = useState({
    inquiryDate: "",
    fullName: "",
    gender: "",
    dob: "",
    contactNumber: "",
    alternateNumber: "",
    email: "",
    address: "",
    education: "",
    occupation: "",
    courses: "",
    mode: "",
    knowledge: "",
    batchTiming: "",
    startDate: "",
    source: "",
    remarks: "",
    inquiryTakenBy: "",
  });

  const [submitted, setSubmitted] = useState(false); // ‚úÖ Track success

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/inquiries", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await res.json();

      if (res.ok) {
        setSubmitted(true);         // ‚úÖ Show Thank You screen
        setFormData({              // ‚úÖ Reset form
          inquiryDate: "",
          fullName: "",
          gender: "",
          dob: "",
          contactNumber: "",
          alternateNumber: "",
          email: "",
          address: "",
          education: "",
          occupation: "",
          courses: "",
          mode: "",
          knowledge: "",
          batchTiming: "",
          startDate: "",
          source: "",
          remarks: "",
          inquiryTakenBy: "",
        });
      } else {
        console.error("Error:", data.error);
        alert("‚ö†Ô∏è Failed: " + data.error);
      }
    } catch (err) {
      console.error("Error submitting inquiry:", err);
    }
  };

  if (submitted) {
    return (
      <div className="success-container">
        <h2>‚úÖ Thank You!</h2>
        <p>Your inquiry has been submitted successfully.</p>
        <button onClick={() => setSubmitted(false)}>Submit Another Inquiry</button>
      </div>
    );
  }

  return (
    <div className="form-container">
      <h2>üìã IT Academy - Inquiry Form</h2>
      <form onSubmit={handleSubmit} className="inquiry-form">
        {/* form fields (same as before) */}
        {/* ... */}
        <div className="form-actions">
          <button type="submit">Submit Inquiry</button>
        </div>
      </form>
    </div>
  );
};

export default InquiryForm;
