import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    studentId: "",
    total: "",
    deposit: "",
    monthly: "",
    final: "",
    notes: "",
  });
  const [feeStatus, setFeeStatus] = useState("Pending");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  // ‚úÖ Fetch students
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        const data = Array.isArray(res.data) ? res.data : res.data.users || [];
        setStudents(data);
      } catch (err) {
        console.error("Error fetching students:", err);
        setMessage("‚ùå Failed to load students.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  // ‚úÖ Handle search input
  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (!query) {
      setSuggestions([]);
      setFormData((prev) => ({ ...prev, studentId: "" }));
      return;
    }

    const filtered = students.filter(
      (s) =>
        s.fullName?.toLowerCase().includes(query.toLowerCase()) ||
        s.email?.toLowerCase().includes(query.toLowerCase())
    );
    setSuggestions(filtered);
  };

  // ‚úÖ Select student
  const handleSelectStudent = (student) => {
    setSearchQuery(`${student.fullName} (${student.email})`);
    setFormData((prev) => ({ ...prev, studentId: student._id }));
    setSuggestions([]);

    // Fetch student payment info
    axios
      .get(`http://localhost:5000/api/payments/${student._id}`)
      .then((res) => {
        const data = res.data;
        if (data.fees) {
          setFormData({
            studentId: student._id,
            total: data.fees.total || "",
            deposit: data.fees.deposit || "",
            monthly: data.fees.monthly || "",
            final: data.fees.final || "",
            notes: data.fees.notes || "",
          });
          setFeeStatus(data.fees.status || "Pending");
        } else {
          setFormData({
            studentId: student._id,
            total: "",
            deposit: "",
            monthly: "",
            final: "",
            notes: "",
          });
          setFeeStatus("Pending");
        }
      })
      .catch((err) => console.error("Error fetching fee info:", err));
  };

  return (
    <div style={{ maxWidth: "500px", margin: "20px auto" }}>
      <h2>üí≥ Manage Student Fees</h2>
      {message && <p>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <form>
          {/* Search Box */}
          <div style={{ position: "relative", marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Search student by name/email"
              value={searchQuery}
              onChange={handleSearchChange}
              style={{ width: "100%", padding: "8px", borderRadius: "4px" }}
            />
            {suggestions.length > 0 && (
              <ul
                style={{
                  position: "absolute",
                  top: "100%",
                  left: 0,
                  right: 0,
                  background: "#fff",
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                  listStyle: "none",
                  margin: 0,
                  padding: 0,
                  maxHeight: "150px",
                  overflowY: "auto",
                  zIndex: 1000,
                }}
              >
                {suggestions.map((s) => (
                  <li
                    key={s._id}
                    onMouseDown={(e) => e.preventDefault()}
                    onClick={() => handleSelectStudent(s)}
                    style={{ padding: "8px", cursor: "pointer" }}
                  >
                    {s.fullName} ({s.email})
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Fee Inputs */}
          <input
            type="number"
            name="total"
            placeholder="Total Fee"
            value={formData.total}
            onChange={(e) => setFormData({ ...formData, total: e.target.value })}
          />
          <input
            type="number"
            name="deposit"
            placeholder="Deposit"
            value={formData.deposit}
            onChange={(e) => setFormData({ ...formData, deposit: e.target.value })}
          />
          <input
            type="number"
            name="monthly"
            placeholder="Monthly Fee"
            value={formData.monthly}
            onChange={(e) => setFormData({ ...formData, monthly: e.target.value })}
          />
          <input
            type="number"
            name="final"
            placeholder="Final Payment"
            value={formData.final}
            onChange={(e) => setFormData({ ...formData, final: e.target.value })}
          />
        </form>
      )}
    </div>
  );
};

export default AdminPayments;
