import "./App.css";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// üîê Auth Screens
import Login from "./screens/Auth/Login.jsx";
import Signup from "./screens/Auth/Signup.jsx";
import ForgotPassword from "./screens/Auth/ForgotPassword.jsx";
import LoginOptions from "./screens/Auth/LoginOptions.jsx";

// üéì Student Screens
import StuProfile from "./screens/Student/StuProfile.jsx";
import StuProfileEdit from "./screens/Student/StuProfileEdit.jsx";
import ApplicationForm from "./screens/Student/Application_form.jsx";
import StuPayments from "./screens/Student/StuPayments.jsx";
import Dashboard from "./screens/dashboard/Dashboard.jsx";
import InquiryForm from "./screens/InquiryForm/inquiry_form.jsx";

// üßë‚Äçüíº Admin Screens
import AdminProfile from "./screens/Admin/AdminProfile.jsx";
import AdminInquiries from "./screens/Admin/AdminInquiries.jsx";
import Users from "./screens/Admin/Users.jsx";
import AdminAdmissions from "./screens/Admin/AdminAdmissions.jsx";
import AdminPayments from "./screens/Admin/AdminPayments.jsx";
import AdminReports from "./screens/Admin/Reports.jsx";
import AdminAdmissionForm from "./screens/Admin/AdminAdmissionForm.jsx";

// ‚úÖ Protected Route
import ProtectedRoute from "./components/ProtectedRoute.jsx";

function App() {
  const userRole = localStorage.getItem("role");

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<LoginOptions />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/login-options" element={<LoginOptions />} />

        {/* Student Routes */}
        <Route
          path="/studentprofile"
          element={
            <ProtectedRoute>
              <StuProfile />
            </ProtectedRoute>
          }
        />
        <Route
          path="/studentprofileedit"
          element={
            <ProtectedRoute>
              <StuProfileEdit />
            </ProtectedRoute>
          }
        />
        <Route
          path="/studentapplication"
          element={
            <ProtectedRoute>
              <ApplicationForm />
            </ProtectedRoute>
          }
        />

        <Route 
          path="/admin-admission-form/:id"
          element={
            <ProtectedRoute>
              <AdminAdmissionForm />
            </ProtectedRoute>
          }
        />


        <Route
          path="/studentpayments"
          element={
            <ProtectedRoute>
              <StuPayments />
            </ProtectedRoute>
          }
        />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/inquiry"
          element={
            <ProtectedRoute>
              <InquiryForm />
            </ProtectedRoute>
          }
        />

        {/* Admin Routes */}
        <Route
          path="/admin-profile"
          element={
            <ProtectedRoute>
              <AdminProfile />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin-inquiries"
          element={
            <ProtectedRoute>
              <AdminInquiries />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/users"
          element={
            <ProtectedRoute>
              <Users />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin-admissions"
          element={
            <ProtectedRoute>
              <AdminAdmissions />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin-payments"
          element={
            <ProtectedRoute>
              <AdminPayments />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin-reports"
          element={
            <ProtectedRoute>
              <AdminReports />
            </ProtectedRoute>
          }
        />

        {/* Default Role-Based Redirect */}
        <Route
          path="*"
          element={
            userRole === "Admin" ? (
              <Navigate to="/admin-profile" />
            ) : userRole === "Student" ? (
              <Navigate to="/studentprofile" />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
