import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPayments = () => {
  const [students, setStudents] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [formData, setFormData] = useState({
    studentId: "",
    total: "",
    deposit: "",
    monthly: "",
    final: "",
    notes: "",
  });
  const [feeStatus, setFeeStatus] = useState("Pending");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        console.log("API Response:", res.data);
        if (Array.isArray(res.data)) {
          setStudents(res.data);
        } else {
          setStudents([]);
          setMessage("‚ö†Ô∏è Unexpected data format from API.");
        }
      } catch (error) {
        console.error("Error fetching students:", error);
        setMessage("‚ùå Failed to load students. Check console for details.");
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(e.target.value);

    if (query.length > 0) {
      const filtered = students.filter(
        (s) =>
          s.fullName?.toLowerCase().includes(query) ||
          s.email?.toLowerCase().includes(query) ||
          `${s.fullName} (${s.email})`.toLowerCase().includes(query)
      );
      setSuggestions(filtered);

      if (filtered.length === 1 && 
          (filtered[0].fullName.toLowerCase() === query ||
           filtered[0].email.toLowerCase() === query ||
           `${filtered[0].fullName} (${filtered[0].email})`.toLowerCase() === query)) {
        handleSelectStudent(filtered[0]);
      }
    } else {
      setSuggestions([]);
      setFormData({ ...formData, studentId: "" });
      setFeeStatus("Pending");
    }
  };

  const handleSelectStudent = async (student) => {
    setFormData({ ...formData, studentId: student._id });
    setSearchQuery(`${student.fullName} (${student.email})`);
    setSuggestions([]);

    try {
      const res = await axios.get(`http://localhost:5000/api/payments/${student._id}`);
      const data = await res.json();
      if (data.fees) {
        setFormData({
          studentId: student._id,
          total: data.fees.total || "",
          deposit: data.fees.deposit || "",
          monthly: data.fees.monthly || "",
          final: data.fees.final || "",
          notes: data.fees.notes || "",
        });
        setFeeStatus(data.fees.status || "Pending");
      } else {
        setFormData({ ...formData, studentId: student._id, total: "", deposit: "", monthly: "", final: "", notes: "" });
        setFeeStatus("Pending");
      }
    } catch (error) {
      console.error("Error fetching fee status:", error);
      setFeeStatus("Pending");
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Submitting fee data:", formData);
    if (!formData.studentId || !formData.total || !formData.deposit || !formData.monthly || !formData.final) {
      setMessage("‚ö†Ô∏è Please select a student and fill all fee fields.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/payments/assign-fee", formData, {
        headers: { "Content-Type": "application/json" },
      });
      setMessage("‚úÖ Fee assigned/updated successfully!");
      setFeeStatus(res.data.fee.status); // Update status after assignment
      setTimeout(() => setMessage(""), 3000); // Clear message after 3 seconds
    } catch (error) {
      console.error("Error assigning fee:", {
        message: error.message,
        response: error.response ? error.response.data : "No response",
        status: error.response ? error.response.status : "No status",
      });
      setMessage("‚ùå Failed to assign fee. Check console for details.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>üí≥ Manage Student Fees</h2>
      {message && <p style={styles.message}>{message}</p>}
      {loading ? (
        <p>Loading students...</p>
      ) : (
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={{ position: "relative", marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Search student by name or email"
              value={searchQuery}
              onChange={handleSearchChange}
              style={styles.input}
            />
            {suggestions.length > 0 && (
              <ul style={styles.suggestions}>
                {suggestions.map((s) => (
                  <li
                    key={s._id}
                    onClick={() => handleSelectStudent(s)}
                    onMouseDown={(e) => e.preventDefault()}
                    style={styles.suggestionItem}
                  >
                    {s.fullName} ({s.email})
                  </li>
                ))}
              </ul>
            )}
            {searchQuery.trim() !== "" && suggestions.length === 0 && students.length > 0 && (
              <ul style={styles.suggestions}>
                <li style={{ padding: "8px", color: "#888" }}>
                  No students found
                </li>
              </ul>
            )}
          </div>

          <input
            type="number"
            name="total"
            placeholder="Total Fee"
            value={formData.total}
            onChange={handleChange}
            style={styles.input}
            required
            min="0"
          />
          <input
            type="number"
            name="deposit"
            placeholder="Deposit"
            value={formData.deposit}
            onChange={handleChange}
            style={styles.input}
            required
            min="0"
          />
          <input
            type="number"
            name="monthly"
            placeholder="Monthly Fee"
            value={formData.monthly}
            onChange={handleChange}
            style={styles.input}
            required
            min="0"
          />
          <input
            type="number"
            name="final"
            placeholder="Final Payment"
            value={formData.final}
            onChange={handleChange}
            style={styles.input}
            required
            min="0"
          />
          <textarea
            name="notes"
            placeholder="Notes"
            value={formData.notes}
            onChange={handleChange}
            style={styles.textarea}
          />

          <div style={styles.statusContainer}>
            <p><strong>Current Fee Status:</strong> <span style={styles.status}>{feeStatus}</span></p>
          </div>

          <button type="submit" style={styles.button}>
            Assign/Update Fee
          </button>
        </form>
      )}
    </div>
  );
};

// Inline CSS
const styles = {
  container: {
    maxWidth: "500px",
    margin: "20px auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    backgroundColor: "#fafafa",
  },
  form: { display: "flex", flexDirection: "column" },
  input: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100%",
    boxSizing: "border-box",
  },
  textarea: {
    padding: "10px",
    marginBottom: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    width: "100",
    minHeight: "80px",
    boxSizing: "border-box",
  },
  button: {
    padding: "10px",
    background: "linear-gradient(to right, #6b48ff, #00c4cc)",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontWeight: "bold",
  },
  message: {
    margin: "10px 0",
    fontWeight: "bold",
    color: "#dc3545",
  },
  suggestions: {
    position: "absolute",
    top: "100%",
    left: 0,
    right: 0,
    border: "1px solid #ccc",
    backgroundColor: "#fff",
    borderRadius: "5px",
    listStyle: "none",
    padding: "0",
    margin: "0",
    maxHeight: "150px",
    overflowY: "auto",
    zIndex: "1000",
  },
  suggestionItem: {
    padding: "8px",
    cursor: "pointer",
    "&:hover": {
      backgroundColor: "#f0f0f0",
    },
  },
  statusContainer: {
    marginBottom: "15px",
    padding: "10px",
    background: "#f5f6fa",
    borderRadius: "5px",
  },
  status: {
    padding: "6px 10px",
    borderRadius: "6px",
    fontSize: "14px",
    fontWeight: "bold",
    background: "#ffeaa7", // Default for Pending
  },
};

export default AdminPayments;
