import React, { useEffect, useState } from "react";
import Header from "../../components/Header";

const Payments = () => {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalRevenue, setTotalRevenue] = useState(0);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/payments"); // ‚úÖ your backend payments route
        const data = await res.json();
        setPayments(data);

        // calculate revenue
        const revenue = data
          .filter((p) => p.status === "Completed")
          .reduce((sum, p) => sum + Number(p.amount || 0), 0);
        setTotalRevenue(revenue);

        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching payments:", err);
        setLoading(false);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="page">
      <Header title="üí≥ Payments" />

      {/* Internal CSS */}
      <style>{`
        .payments-container {
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0,0,0,0.1);
          margin-top: 20px;
        }
        .revenue-card {
          background: linear-gradient(135deg, #6c5ce7, #00cec9);
          color: #fff;
          padding: 20px;
          border-radius: 12px;
          font-size: 18px;
          font-weight: bold;
          margin-bottom: 20px;
          text-align: center;
        }
        table {
          width: 100%;
          border-collapse: collapse;
          margin-top: 15px;
        }
        th, td {
          padding: 12px;
          border-bottom: 1px solid #eee;
          text-align: left;
        }
        th {
          background: #f5f6fa;
          font-weight: 600;
        }
        tr:hover {
          background: #f9f9f9;
        }
        .status {
          padding: 6px 10px;
          border-radius: 6px;
          font-size: 14px;
          font-weight: bold;
        }
        .Completed {
          background: #55efc4;
          color: #2d3436;
        }
        .Pending {
          background: #ffeaa7;
          color: #636e72;
        }
        .Failed {
          background: #fab1a0;
          color: #d63031;
        }
      `}</style>

      <div className="payments-container">
        <div className="revenue-card">
          üí∞ Total Revenue: ‚Çπ{totalRevenue}
        </div>

        {loading ? (
          <p>‚è≥ Loading payments...</p>
        ) : payments.length === 0 ? (
          <p>No payments found.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Student</th>
                <th>Email</th>
                <th>Amount (‚Çπ)</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {payments.map((p) => (
                <tr key={p._id}>
                  <td>{p.studentName || "N/A"}</td>
                  <td>{p.email || "N/A"}</td>
                  <td>{p.amount}</td>
                  <td>
                    <span className={`status ${p.status}`}>{p.status}</span>
                  </td>
                  <td>{new Date(p.createdAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Payments;
